import React, { useState } from 'react';
import Calendar from 'react-calendar'; // You can use a React Calendar library for scheduling
import './Contact.css'; // Import your CSS for styling

const Contact = () => {
  // State to manage which tab is active
  const [activeTab, setActiveTab] = useState('call');

  // State to manage form fields for scheduling a call
  const [callForm, setCallForm] = useState({
    name: '',
    email: '',
    phone: '',
    selectedDate: null,
  });

  // State to manage chat messages
  const [chatMessages, setChatMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  // Function to handle tab click
  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  // Function to handle form input changes for scheduling a call
  const handleCallFormChange = (e) => {
    const { name, value } = e.target;
    setCallForm({ ...callForm, [name]: value });
  };

  // Function to handle date selection for scheduling a call
  const handleDateSelect = (date) => {
    setCallForm({ ...callForm, selectedDate: date });
  };

  // Function to submit the call request form
  const submitCallRequest = () => {
    // Implement the logic to send an email request for a call here (backend).
    // After successful submission, you can show a confirmation message to the user.
  };

  // Function to handle sending a chat message
  const sendMessage = () => {
    // Implement the logic to send a message (backend).
    // Update the chatMessages state with the sent message.
  };

  return (
    <div className="contact-container">
      <div className="tab-buttons">
        <button
          className={activeTab === 'call' ? 'active-tab' : ''}
          onClick={() => handleTabClick('call')}
        >
          Schedule a Call
        </button>
        <button
          className={activeTab === 'chat' ? 'active-tab' : ''}
          onClick={() => handleTabClick('chat')}
        >
          Instant Chat
        </button>
      </div>
      <div className="tab-content">
        {activeTab === 'call' && (
          <div className="call-tab">
            <h2>Schedule a Call</h2>
            <form>
              <input
                type="text"
                name="name"
                placeholder="Your Name"
                value={callForm.name}
                onChange={handleCallFormChange}
              />
              <input
                type="email"
                name="email"
                placeholder="Your Email"
                value={callForm.email}
                onChange={handleCallFormChange}
              />
              <input
                type="tel"
                name="phone"
                placeholder="Your Phone"
                value={callForm.phone}
                onChange={handleCallFormChange}
              />
              <Calendar
                onChange={handleDateSelect}
                value={callForm.selectedDate}
              />
              <button onClick={submitCallRequest}>Submit</button>
            </form>
          </div>
        )}
        {activeTab === 'chat' && (
          <div className="chat-tab">
            <h2>Instant Chat</h2>
            <div className="chat-messages">
              {chatMessages.map((message, index) => (
                <div key={index} className="message">
                  {message}
                </div>
              ))}
            </div>
            <input
              type="text"
              placeholder="Type your message..."
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
            />
            <button onClick={sendMessage}>Send</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Contact;
