import React from 'react';
import { View, ScrollView, Text, Image, StyleSheet } from 'react-native';

const Projects = () => {
  const projects = [
    {
      title: 'Project 1',
      description: 'Description of Project 1',
      image: require('./project1.jpg'),
    },
    {
      title: 'Project 2',
      description: 'Description of Project 2',
      image: require('./'),
    },
    // Add more projects here
  ];

  return (
    <View style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollView}>
        <View style={styles.slider}>
          {/* Image slider with descriptions */}
          {projects.map((project, index) => (
            <View key={index} style={styles.slide}>
              <Image source={project.image} style={styles.slideImage} />
              <Text style={styles.slideDescription}>{project.description}</Text>
            </View>
          ))}
        </View>

        <View style={styles.projectCards}>
          {/* Project cards */}
          {projects.map((project, index) => (
            <View key={index} style={styles.projectCard}>
              {/* Card content */}
              <Text style={styles.projectTitle}>{project.title}</Text>
              {/* Additional project card content */}
            </View>
          ))}
        </View>
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f2f2f2',
    alignItems: 'center',
  },
  scrollView: {
    flexGrow: 1,
    alignItems: 'center',
  },
  slider: {
    width: '100%',
    height: 200,
    alignItems: 'center',
  },
  slide: {
    width: '80%',
    height: '100%',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#333',
    marginHorizontal: 10,
    borderRadius: 10,
  },
  slideImage: {
    width: '100%',
    height: '80%',
    resizeMode: 'cover',
    borderRadius: 10,
  },
  slideDescription: {
    color: 'white',
    fontSize: 16,
    marginTop: 10,
    textAlign: 'center',
  },
  projectCards: {
    width: '100%',
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-evenly',
    marginTop: 20,
  },
  projectCard: {
    width: '30%',
    backgroundColor: 'white',
    padding: 20,
    marginBottom: 20,
    borderRadius: 10,
    alignItems: 'center',
  },
  projectTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
});

export default Projects;
