import React, { useState } from 'react';
import './projects.css';

const projects = [
  {
    title: 'Project 1',
    description: 'Description of Project 1',
    image: 'project1.jpg', // Replace with the actual image path
  },
  {
    title: 'Project 2',
    description: 'Description of Project 2',
    image: 'project2.jpg', // Replace with the actual image path
  },
  {
    title: 'Project 3',
    description: 'Description of Project 3',
    image: 'project3.jpg', // Replace with the actual image path
  },
  {
    title: 'Project 4',
    description: 'Description of Project 4',
    image: 'project4.jpg', // Replace with the actual image path
  },
  // Add more projects here
];

const topAchievements = [
  {
    title: 'Achievement 1',
    description: 'Description of Achievement 1',
    image: 'achievement1.jpg', // Replace with the actual image path
  },
  {
    title: 'Achievement 2',
    description: 'Description of Achievement 2',
    image: 'achievement2.jpg', // Replace with the actual image path
  },
  {
    title: 'Achievement 3',
    description: 'Description of Achievement 3',
    image: 'achievement3.jpg', // Replace with the actual image path
  },
  // Add more top achievements here
];

const Projects = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const goToPrevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === 0 ? topAchievements.length - 1 : prevSlide - 1));
  };

  const goToNextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === topAchievements.length - 1 ? 0 : prevSlide + 1));
  };

  return (
    <div className="projects-container">
      <div className="projects">
        {projects.map((project, index) => (
          <div className="project-card" key={index}>
            <h2>{project.title}</h2>
            <p>{project.description}</p>
            <img src={project.image} alt={project.title} />
          </div>
        ))}
      </div>

    </div>
  );
};

export default Projects;
