import React, { useState } from "react";
import "./Blogs.css"; // Import the CSS file
import { FaTrash, FaPencilAlt } from "react-icons/fa";

const Blogs = () => {
  // Sample blog data (you can replace this with your actual blog data)
  const blogs = [
    {
      id: 1,
      title: "Blog Title 1",
      content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit...",
    },
    {
      id: 2,
      title: "Blog Title 2",
      content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit...",
    },
    // Add more blog entries here
  ];

  // State for managing comments and likes
  const [comments, setComments] = useState([]);
  const [likes, setLikes] = useState({});

  // State for tracking the visibility of the comment section
  const [isCommentSectionVisible, setIsCommentSectionVisible] = useState(false);
  const [commentText, setCommentText] = useState(""); // State to store comment text

  // Function to toggle the comment section visibility
  const toggleCommentSection = () => {
    setIsCommentSectionVisible(!isCommentSectionVisible);
  };

  // Function to add a new comment
  const addComment = (blogId, commentText) => {
    // Create a new comment object
    const newComment = {
      id: Math.random(), // You can generate a unique ID using a library or a backend service later
      blogId,
      text: commentText,
      likes: 0, // Initialize likes to 0
    };

    // Add the new comment to the comments array
    setComments([...comments, newComment]);
  };

  // Function to handle liking a comment
  const handleLike = (commentId) => {
    // Update the likes for the given comment
    setLikes({
      ...likes,
      [commentId]: (likes[commentId] || 0) + 1, // Increment the likes count
    });
  };

  // Function to delete a comment
  const deleteComment = (commentId) => {
    // Filter out the comment with the given ID
    const updatedComments = comments.filter(
      (comment) => comment.id !== commentId
    );

    // Update the comments state
    setComments(updatedComments);
  };

  return (
    <div className="blogs-container">
      {blogs.map((blog) => (
        <div key={blog.id} className="blog">
          <h2>{blog.title}</h2>
          <p>{blog.content}</p>

          {/* Add a Comment button to toggle the comment section */}
          <button onClick={toggleCommentSection}>
            {isCommentSectionVisible ? "Hide Comments" : "Add a Comment"}
          </button>
          {/* Comment section */}
          <div className="comment-section">
            <h3>Comments</h3>
            <ul>
              {comments
                .filter((comment) => comment.blogId === blog.id)
                .map((comment) => (
                  <li key={comment.id} className="comment">
                    <div className="comment-content">
                      <p>{comment.text}</p>
                      <span className="like-count">
                        Likes: {likes[comment.id] || 0}
                      </span>
                    </div>
                    <div className="comment-buttons">
                      <button onClick={() => deleteComment(comment.id)}>
                        <FaTrash /> Delete
                      </button>
                      <button onClick={() => handleLike(comment.id)}>üëç</button>
                      <button>
                        Edit <FaPencilAlt />
                      </button>
                    </div>
                  </li>
                ))}
            </ul>
            <form
              onSubmit={(e) => {
                e.preventDefault();
                const commentText = e.target.commentText.value;
                addComment(blog.id, commentText);
                e.target.commentText.value = ""; // Clear the input field
              }}
            >
              <input
                type="text"
                name="commentText"
                placeholder="Add a comment"
                className="comment-input"
              />
              <button type="submit" className="comment-button">
                Post
              </button>
            </form>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Blogs;
