import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import { Link } from 'react-router-dom';

import './ScheduleCall.css';

const time = ['08:00', '09:00', '10:00', '11:00', '12:00', '1:00', '2:00', '3:00', '4:00'];

function Times(props) {
  const [event, setEvent] = useState(null);
  const [info, setInfo] = useState(false);
  const [date, setDate] = useState(new Date());

  function displayInfo(e) {
    setInfo(true);
    setEvent(e.target.innerText);
  }

  return (
    <div className="times">
      {time.map((times) => {
        return (
          <div key={times}>
            <button className="custom-button" onClick={(e) => displayInfo(e)}>
              {times}
            </button>
          </div>
        );
      })}
      <div>{info ? `Your snuggle is set to ${event} ${props.date.toDateString()}` : null}</div>
    </div>
  );
}

function ScheduleCall() {
  const [date, setDate] = useState({ startDate: null, endDate: null });
  const [showTime, setShowTime] = useState(false);

  const maxDate = new Date(); // Get the current date
  maxDate.setMonth(maxDate.getMonth() + 1); // Add one month to the current date

  return (
    <div className="schedule-call-container">
      <div className="calendar-card">
        <div className="calendar">
          <h3 className="header">Booking Calendar</h3>
          <div className="calendar-container">
            <Calendar
              onChange={(date) => setDate({ startDate: date, endDate: null })}
              value={date.startDate}
              onClickDay={() => setShowTime(true)}
              maxDate={maxDate} // Set the max date here
            />
          </div>
          {date.startDate && date.endDate && (
            <p>
              <span>Start:</span>
              {date.startDate.toDateString()} &nbsp; &nbsp;
              <span>End:</span>
              {date.endDate.toDateString()}
            </p>
          )}
        </div>
      </div>
      <div className="times-card">
        <Times showTime={showTime} date={date.startDate} />
        <Link to="/schedule">
          <button className="custom-button">
            Schedule a Meeting
          </button>
        </Link>
      </div>
    </div>
  );
}

export default ScheduleCall;
