import React, { useState } from 'react'; // You can use a React Calendar library for scheduling
import './Contact.css'; // Import your CSS for styling
import InstantChat from './InstantChat';
import ScheduleCall from './ScheduleCall';

const Contact = () => {
  // State to manage which tab is active
  const [activeTab, setActiveTab] = useState('call');

  // Function to handle tab click
  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="contact-container">
      <div className="tab-buttons">
        <button
          className={activeTab === 'call' ? 'active-tab' : ''}
          onClick={() => handleTabClick('call')}
        >
           <ScheduleCall/>
        </button>
        <button
          className={activeTab === 'chat' ? 'active-tab' : ''}
          onClick={() => handleTabClick('chat')}
        >
         <InstantChat/>
        </button>
      </div>
      <div className="tab-content">

      </div>
    </div>
  );
};

export default Contact;
